import React, { useState, useEffect } from "react";
import "../../App.css";
import "../Profile.css";
import Card from "../Card";

export default function Profile() {
  const [bio, setBio] = useState(""); // State to store the bio value

  useEffect(() => {
    // Fetch the bio value when the component mounts
    const fetchData = async () => {
      try {
        const response = await fetch('http://127.0.0.1:5000/advisee/bio/"sup"');

        if (!response.ok) {
          throw new Error(`Failed to fetch bio. Status: ${response.status}`);
        }

        const data = await response.json();

        // Assuming the JSON structure is as expected
        setBio(data.bioAdvisees[0]);
      } catch (error) {
        console.error("Error fetching bio:", error.message);
      }
    };

    fetchData();
  }, []);

  const cardData = {
    type: "Student",
    profilePicture: "../../../images/profile-icon.jpg",
    name: "John Doe",
    researchInterests: "Machine Learning",
    educationLevel: "Ph.D.",
    school: "McGill Univeristy",
    fieldOfStudy: "Computer Science",
    bio: bio,
  };
  return (
    <div className="profile">
      <h1 className="profile">PROFILE</h1>
      <Card {...cardData} />
    </div>
  );
}
