import React, { useState, useEffect } from "react";
import "../../App.css";
import "../Profile.css";
import Card from "../Card";

export default function Profile() {
  const [bio, setBio] = useState(""); // State to store the bio value
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [interest1, setInterest1] = useState("");
  const [interest2, setInterest2] = useState("");
  const [interest3, setInterest3] = useState("");
  const [school, setSchool] = useState("");

  useEffect(() => {
    // Fetch the bio value when the component mounts
    const fetchData = async () => {
      try {
        // Fetch bio data
        const bioResponse = await fetch(
          'http://127.0.0.1:5000/advisee/bio/"sup"'
        );
        if (!bioResponse.ok) {
          throw new Error(`Failed to fetch bio. Status: ${bioResponse.status}`);
        }
        const bioData = await bioResponse.json();

        // Assuming the JSON structure is as expected
        setBio(bioData.bioAdvisees[0]);

        // Fetch school data
        const schoolResponse = await fetch(
          'http://127.0.0.1:5000/advisee/school/"sup"'
        );
        if (!schoolResponse.ok) {
          throw new Error(
            `Failed to fetch school. Status: ${schoolResponse.status}`
          );
        }
        const schoolData = await schoolResponse.json();

        // Assuming the JSON structure is as expected
        setSchool(schoolData.schoolAdvisees[0]);
      } catch (error) {
        console.error("Error fetching data:", error.message);
      }

      try {
        const response = await fetch(
          'http://127.0.0.1:5000/advisee/firstname/"sup"'
        );
        if (!response.ok) {
          throw new Error(
            `Failed to fetch firstname. Status: ${response.status}`
          );
        }
        const data = await response.json();

        // Assuming the JSON structure is as expected
        setFirstName(data.firstNameAdvisees[0]);
      } catch (error) {
        console.error("Error fetching firstName:", error.message);
      }
      try {
        const response = await fetch(
          'http://127.0.0.1:5000/advisee/lastname/"sup"'
        );
        if (!response.ok) {
          throw new Error(
            `Failed to fetch lastname. Status: ${response.status}`
          );
        }
        const data = await response.json();

        // Assuming the JSON structure is as expected
        setLastName(data.lastNameAdvisees[0]);
      } catch (error) {
        console.error("Error fetching lastName:", error.message);
      }
      try {
        const response = await fetch(
          'http://127.0.0.1:5000/advisee/interest1/"sup"'
        );
        if (!response.ok) {
          throw new Error(
            `Failed to fetch interest1. Status: ${response.status}`
          );
        }
        const data = await response.json();

        // Assuming the JSON structure is as expected
        setInterest1(data.interest1Advisees[0]);
      } catch (error) {
        console.error("Error fetching interest1:", error.message);
      }
      try {
        const response = await fetch(
          'http://127.0.0.1:5000/advisee/interest2/"sup"'
        );
        if (!response.ok) {
          throw new Error(
            `Failed to fetch interest2. Status: ${response.status}`
          );
        }
        const data = await response.json();

        // Assuming the JSON structure is as expected
        setInterest2(data.interest2Advisees[0]);
      } catch (error) {
        console.error("Error fetching interest2:", error.message);
      }
      try {
        const response = await fetch(
          'http://127.0.0.1:5000/advisee/interest3/"sup"'
        );
        if (!response.ok) {
          throw new Error(
            `Failed to fetch interest3. Status: ${response.status}`
          );
        }
        const data = await response.json();

        // Assuming the JSON structure is as expected
        setInterest3(data.interest3Advisees[0]);
      } catch (error) {
        console.error("Error fetching interest3:", error.message);
      }
    };

    fetchData();
  }, []);

  const cardData = {
    type: "Student",
    profilePicture: "../../../images/profile-icon.jpg",
    name: firstName + lastName,
    researchInterests: interest1 + ", " + interest2 + "," + interest3,
    educationLevel: "Ph.D.",
    school: school,
    fieldOfStudy: "Computer Science",
    bio: bio,
  };
  return (
    <div className="profile">
      <h1 className="profile">PROFILE</h1>
      <Card {...cardData} />
    </div>
  );
}
